mapper.select:
SELECT i_user, user_id, user_pw, salt, nm, user_type, date_format(bd, "%Y년 %c월 %e일") as bd,
profile_img, date_format(r_dt, "%Y년 %c월 %e일") as r_dt, date_format(m_dt, "%Y년 %c월 %e일") as m_dt
FROM t_user
WHERE  i_user = #{i_user}
user_id = #{user_id}

mapper.update:
UPDATE t_user
SET
nm = #{nm}

,user_pw = #{user_pw}
,salt = #{salt}


,profile_img = #{profile_img}

WHERE i_user = #{i_user}

mapper.delete:
DELETE FROM t_user_favorite_perfume
WHERE i_p = #{i_p}
AND i_user = #{i_user}

DELETE FROM t_user_favorite_note
WHERE i_user = #{i_user}
AND nt_m_c = #{nt_m_c};
